class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each(function(){t.deleteComment($(this))})}createComment(e){let t=this;this.newCommentForm.submit(function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).prepend(o),pSelf.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-btn",o))},error:function(e){console.log("error in create comment ajax ",e.responseText)}})})}newCommentDom(e){return $(`\n            <li id="comment-${e._id}">\n                <p>\n                    <small>\n                        <a class="delete-comment-button" href="/comments/destroy/${e._id}"> X </a>\n                    </small>\n                    ${e.content}\n                    <br>\n                    <small>\n                        ${e.user.name}\n                    </small>\n                    <br>\n                    <small>\n                        <a class="toggle-like-btn" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                            0 Likes\n                        </a>\n                    </small>\n                </p>    \n            </li>\n        `)}deleteComment(e){$(e).click(function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment._id}`).remove()},error:function(e){console.log(error.responseText)}})})}}